const Header = (props) => {
  return (
    <div>
      <h1>{props.c}</h1>
    </div>
  )
}

const Part = (props) => {
  console.log(props.name)
  return (
    <div>
      <p>
        {props.name} {props.count}
      </p>
    </div>
  )
}

const Content = (props) => {
  const cp = props.courseParts
  // console.log(cp[0].name)
  return (
    <div>
        <Part name = {cp[0].name} count = {cp[0].exercises} />
        <Part name = {cp[1].name} count = {cp[1].exercises} />
        <Part name = {cp[2].name} count = {cp[2].exercises} />
    </div>
    )
}

const Total = (props) => {
  const exsum = 0
  props.courseParts.forEach(i => {
    exsum += courseParts[i].exercises
  });
  console.log(exsum)

  return (
    <div>
      <p>
        Number of exercises {exsum}
      </p>
    </div>
  )
}

const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10
      },
      {
        name: 'Using props to pass data',
        exercises: 7
      },
      {
        name: 'State of a component',
        exercises: 14
      }
    ]
  }

  return (
    <div>
      <Header courseName = {course.name}/>
      <Content courseParts = {course.parts}/>
      <Total courseParts = {course.parts}/>
    </div>
  )
}
export default App
