const Header = (props) => {
  return (
    <div>
      <h1>{props.c}</h1>
    </div>
  )
}

const Part = (props) => {
  return (
    <div>
      <p>
        {props.name} {props.count}
      </p>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
        <Part name = {props.p[0].name} count = {props.p[0].exercises} />
        <Part name = {props.p[1].name} count={props.p[1].exercises} />
        <Part name = {props.p[2].name} count={props.p[2].exercises} />
    </div>
    )
}

const Total = (props) => {
  const exsum = props.p[0].exercises + props.p[1].exercises + props.p[2].exercises

  return (
    <div>
      <p>
        Number of exercises {exsum}
      </p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <Header c = {course}/>
      <Content p = {parts}/>
      <Total sum p = {parts}/>
    </div>
  )
}
export default App
