const Header = (props) => {
  return (
    <div>
      <h1>{props.c}</h1>
    </div>
  )
}

const Part = (props) => {
  return (
    <div>
      <p>
        {props.name} {props.count}
      </p>
    </div>
  )
}

const Content = (props) => {
  const courseParts = props.c.parts
  console.log(courseParts)
  return (
    <div>
        <Part name = {courseParts[0].name} count = {courseParts[0].exercises} />
        <Part name = {courseParts[1].name} count = {courseParts[1].exercises} />
        <Part name = {courseParts[2].name} count = {courseParts[2].exercises} />
    </div>
    )
}

const Total = (props) => {
  const exsum = props.p[0].exercises + props.p[1].exercises + props.p[2].exercises

  return (
    <div>
      <p>
        Number of exercises {exsum}
      </p>
    </div>
  )
}

const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10
      },
      {
        name: 'Using props to pass data',
        exercises: 7
      },
      {
        name: 'State of a component',
        exercises: 14
      }
    ]
  }

  return (
    <div>
      <Header c = {course.name}/>
      <Content c = {course}/>
      <Total c = {course}/>
    </div>
  )
}
export default App
